function createSelect(paises) {
    const select = document.querySelector('#paises');
    for (const [value, text] of Object.entries(paises)) {
      const option = document.createElement("option");
      option.value = value;
      option.text = text;
      select.appendChild(option);
    }
    return select;
  }
